ass=\"wp-block-heading has-text-align-center superbthemes-navigation-004-headline has-contrast-light-color has-text-color\" style=\"padding-right:0;padding-bottom:0;padding-left:0;font-size:100px;font-style:normal;font-weight:500;letter-spacing:30px\"><code>AITANA</code></h1>\n<!-- /wp:heading --></div></div>\n<!-- /wp:cover --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|superbspacing-xxlarge\",\"bottom\":\"var:preset|spacing|superbspacing-xxlarge\",\"left\":\"var:preset|spacing|superbspacing-small\",\"right\":\"var:preset|spacing|superbspacing-small\"},\"blockGap\":\"var:preset|spacing|superbspacing-large\",\"margin\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\" style=\"margin-top:0;margin-bottom:0;padding-top:var(--wp--preset--spacing--superbspacing-xxlarge);padding-right:var(--wp--preset--spacing--superbspacing-small);padding-bottom:var(--wp--preset--spacing--superbspacing-xxlarge);padding-left:var(--wp--preset--spacing--superbspacing-small)\"><!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"40%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:40%\"><!-- wp:heading {\"textAlign\":\"left\",\"textColor\":\"contrast\",\"fontSize\":\"superbfont-xlarge\"} -->\n<h2 class=\"wp-block-heading has-text-align-left has-contrast-color has-text-color has-superbfont-xlarge-font-size\">No es solo música, es una forma de vivirla</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|superbspacing-xsmall\"}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"left\":\"var:preset|spacing|superbspacing-xxsmall\"}},\"border\":{\"left\":{\"color\":\"var:preset|color|featured\",\"width\":\"4px\"},\"top\":[],\"right\":[],\"bottom\":[]}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"border-left-color:var(--wp--preset--color--featured);border-left-width:4px;padding-left:var(--wp--preset--spacing--superbspacing-xxsmall)\"><!-- wp:paragraph {\"align\":\"left\",\"textColor\":\"secondary\",\"fontSize\":\"superbfont-small\"} -->\n<p class=\"has-text-align-left has-secondary-color has-text-color has-superbfont-small-font-size\"><strong><em>Historias, letras y experiencias que conectan con todos desde el primer verso</em></strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Descubre sus mayores proyectos</h2>\n<!-- /wp:heading -->\n\n<!-- wp:columns {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|superbspacing-xlarge\",\"left\":\"var:preset|spacing|superbspacing-small\"}}}} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"verticalAlignment\":\"center\",\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|superbspacing-xsmall\"}}} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:gallery {\"linkTo\":\"none\",\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-gallery has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":7,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/aitana/wp-content/uploads/2025/12/cuarto_azul.png\" alt=\"\" class=\"wp-image-7\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|superbspacing-xxsmall\"}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"textColor\":\"contrast\",\"fontSize\":